import React,{Component}from 'react';
import axios from 'axios';
// const apiKey="acace3393ded528905dd2a68775695f1";
// const apiUrl="api.openweathermap.org/data/2.5";


class App extends Component {
  state={
    weather:{},
  }
  componentDidMount(){
    this.getData();
  }
  getData=()=>{
    axios
    .get(`https://api.openweathermap.org/data/2.5/weather?q=vancouver&appid=acace3393ded528905dd2a68775695f1`)
    .then((response)=>{
      console.log(response.data);
      this.setState({        
        weather:response.data,
      })
    })
  }

  render(){
  return (
    <ul>
    <h1>Vancouver Weather</h1>

    {this.state.weather.main.map((data)=>(
      <li key={data.id}>
        {data.description}
        {data.main}
      </li>
    ))}
    </ul>
  );
}
}

export default App;
